      <Route path="/net-calculator" component={NetCalculator} />
      <Route path="/timer" component={Timer} />
      <Route path="/yks-konular" component={YKSKonular} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={sorguIstemcisi}>
      <ThemeProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;


