  const [tytData, setTytData] = useState<TYTData>({
    T√ºrk√ße: { dogru: 0, yanlis: 0 },
    sosyal: { dogru: 0, yanlis: 0 },
    matematik: { dogru: 0, yanlis: 0 },
    fenBilimleri: { dogru: 0, yanlis: 0 }
  });

