// ============================================================================
// YARDIMCI FONKSƒ∞YONLAR
// ============================================================================

async function bekle(ms: number) {
  await new Promise(resolve => setTimeout(resolve, ms));
}

async function sayfayaGit(page: Page, path: string, baslik: string) {
  console.log(`\nüìÑ ${baslik} sayfasƒ±na gidiliyor...`);
  await page.goto(`${BASE_URL}${path}`);
  await page.waitForLoadState('domcontentloaded');
  await page.waitForTimeout(1000);
}

async function apiYanitKontrol(page: Page, endpoint: string): Promise<boolean> {
  try {
    const fullUrl = `${BASE_URL}${endpoint}`;
    const response = await page.request.get(fullUrl);
    const basarili = response.ok();
    console.log(`${basarili ? '‚úÖ' : '‚ùå'} API: ${endpoint} - Durum: ${response.status()}`);
    return basarili;
  } catch (error) {
    console.log(`‚ùå API Hatasƒ±: ${endpoint}`);
    return false;
  }
}

async function modalBekle(page: Page, timeout = 5000) {
  try {
    await page.locator('[role="dialog"]').first().waitFor({ state: 'visible', timeout });
    await bekle(500);
    return true;
  } catch {
    return false;
  }
}

async function inputDoldur(page: Page, selector: string, deger: string, aciklama: string) {
  try {
    const input = page.locator(selector).first();
    await input.waitFor({ state: 'visible', timeout: 3000 });
    await input.clear();
    await input.fill(deger);
    console.log(`   ‚úÖ ${aciklama}: ${deger}`);
    return true;
  } catch {
    console.log(`   ‚ö†Ô∏è  ${aciklama} alanƒ± bulunamadƒ±`);
    return false;
  }
}

// ============================================================================
// TEST SENARYOLARƒ± - TAM KAPSAMLI
// ============================================================================

test.describe('üî• √áOK KAPSAMLI Sƒ∞STEM TESTƒ∞ - YKS Analiz Takip Sistemi', () => {

  test.beforeEach(async ({ page }) => {
    console.log('\n' + '='.repeat(80));
    await page.goto(BASE_URL);
    await page.waitForLoadState('domcontentloaded');
    await page.waitForTimeout(1500);
  });

  test('1Ô∏è‚É£ BACKEND API - T√ºm Endpoint Testleri', async ({ page }) => {
    console.log('\nüîç BACKEND API TESTLERƒ∞ BA≈ûLIYOR...\n');

    const apiEndpoints = [
      '/api/tasks',
      '/api/question-logs',
      '/api/exam-results',
      '/api/study-hours',
      '/api/weather',
      '/api/calendar',
      '/api/flashcards',
      '/api/topics',
      '/api/pomodoro',
      '/api/alarms'
    ];

    let basariliSayisi = 0;

    for (const endpoint of apiEndpoints) {
      const basarili = await apiYanitKontrol(page, endpoint);
      if (basarili) basariliSayisi++;
      await bekle(200);
    }

    console.log(`\nüìä SONU√á: ${basariliSayisi}/${apiEndpoints.length} API endpoint ba≈üarƒ±lƒ±`);
    expect(basariliSayisi).toBeGreaterThan(0);
