import dotenv from "dotenv";

const originalConsoleLog = console.log;
console.log = (...args: any[]) => {
  const message = args.join(' ');
  if (message.includes('[dotenv') || message.includes('injecting env') || message.includes('dotenvx.com') || message.includes('prevent building')) {
    return;
  }
  originalConsoleLog.apply(console, args);
};

dotenv.config({ debug: false });

console.log = originalConsoleLog;

import express from "express";
import { registerRoutes } from "./rotalar";
import { log, serveStatic } from "./static";
import { validateEnvironmentVariables } from "./env-validation";
import { storage } from "./depolama";

validateEnvironmentVariables();

const app = express();

if (process.env.NODE_ENV === "production") {
  app.set("env", "production");
}

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const pathReq = req.path;
  let capturedJsonResponse: any;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (pathReq.startsWith("/api")) {
      // Gereksiz loglarƒ± filtrele
      const shouldSkipLog = (
        // GET isteklerini ve 304 (Not Modified) yanƒ±tlarƒ±nƒ± atla
        (req.method === 'GET' && res.statusCode === 304) ||
        // Hƒ±zlƒ± GET isteklerini atla (< 50ms)
        (req.method === 'GET' && duration < 50 && res.statusCode === 200)
      );

      if (shouldSkipLog) {
        return;
      }

      const externalIp = req.headers['x-forwarded-for'] || req.socket.remoteAddress || 'unknown';

      // Sadece √∂nemli bilgileri logla
      let logLine = `${req.method} ${pathReq} ${res.statusCode} in ${duration}ms`;

      // Hata durumlarƒ±nda veya yava≈ü isteklerde detay ekle
      if (res.statusCode >= 400 || duration > 1000) {
        logLine += ` [IP: ${externalIp}]`;
      }

      // Sadece hata durumlarƒ±nda response body'yi logla
      if (res.statusCode >= 400 && capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 200) {
        logLine = logLine.slice(0, 199) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: any, res: any, _next: any) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    console.error("Server error:", err);
    res.status(status).json({ message });
  });

  if (app.get("env") === "development") {
    const { setupVite } = await import("./vite");
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  const port = parseInt(process.env.PORT || "5000", 10);
  const host = process.env.HOST || "0.0.0.0";

  server.listen(port, host, () => {
    log(`Dersime d√∂nebilirim !!! Site Link : http://${host}:${port}`);
  });

  // Otomatik ar≈üivleme zamanlayƒ±cƒ±sƒ± - Her Pazar 23:59'da √ßalƒ±≈üƒ±r (T√ºrkiye saati GMT+3)
  function scheduleAutoArchive() {
    // T√ºrkiye saati i√ßin tarih hesaplama
    const now = new Date();
    const turkeyTime = new Date(now.toLocaleString('en-US', { timeZone: 'Europe/Istanbul' }));

    // Bir sonraki Pazar 23:59'u bul
    const nextSunday = new Date(turkeyTime);
    const currentDay = nextSunday.getDay(); // 0 = Pazar, 1 = Pazartesi, ..., 6 = Cumartesi

    // Bug√ºn Pazar ise ve saat 23:59'u ge√ßmemi≈üse, bug√ºn ar≈üivle
    // Bug√ºn Pazar ise ve saat 23:59'u ge√ßtiyse, gelecek Pazar ar≈üivle
    // Diƒüer g√ºnlerdeyse, bu haftanƒ±n veya gelecek haftanƒ±n Pazarƒ±na g√∂re hesapla
    let daysUntilSunday: number;
    if (currentDay === 0) {
      // Pazar g√ºn√º
      const targetTime = new Date(turkeyTime);
      targetTime.setHours(23, 59, 0, 0);
      daysUntilSunday = turkeyTime < targetTime ? 0 : 7;
    } else {
      // Pazar deƒüil
      daysUntilSunday = 7 - currentDay;
    }

    nextSunday.setDate(nextSunday.getDate() + daysUntilSunday);
    nextSunday.setHours(23, 59, 0, 0);

    const msUntilSunday = nextSunday.getTime() - turkeyTime.getTime();

    setTimeout(() => {
      log("üìÖ Pazar 23:59 - Haftalƒ±k otomatik ar≈üivleme ba≈ülatƒ±lƒ±yor...");
      storage.autoArchiveOldData()
        .then(() => {
          log("‚úÖ Haftalƒ±k otomatik ar≈üivleme tamamlandƒ±");
        })
        .catch((error) => {
          console.error("‚ùå Haftalƒ±k otomatik ar≈üivleme hatasƒ±:", error);
        });

      // Bir sonraki hafta i√ßin tekrar zamanla (7 g√ºn = 604800000 ms)
      setInterval(() => {
        log("üìÖ Pazar 23:59 - Haftalƒ±k otomatik ar≈üivleme ba≈ülatƒ±lƒ±yor...");
        storage.autoArchiveOldData()
          .then(() => {
            log("‚úÖ Haftalƒ±k otomatik ar≈üivleme tamamlandƒ±");
          })
          .catch((error) => {
            console.error("‚ùå Haftalƒ±k otomatik ar≈üivleme hatasƒ±:", error);
          });
      }, 7 * 24 * 60 * 60 * 1000); // 7 g√ºn
    }, msUntilSunday);

    const hoursUntil = Math.round(msUntilSunday / 1000 / 60 / 60);
    const daysUntil = Math.floor(hoursUntil / 24);
    log(`BERAT CANKIR √ñZEL ANALƒ∞Z TAKƒ∞P Sƒ∞STEMƒ∞Nƒ∞Z GAYET G√úZEL √áALI≈ûIYOR ƒ∞Yƒ∞ DERSLER Dƒ∞LERƒ∞M :) .`);
  }

  scheduleAutoArchive();
})();


