  }, [allQuestionLogs, allTasks, allExamResults, allStudyHours]);

  // Modal a√ßƒ±kken veriler deƒüi≈ütiƒüinde (√∂r. g√∂rev ar≈üivlendiƒüinde) modal i√ßeriƒüini g√ºncelle
  useEffect(() => {
