export function validateEnvironmentVariables() {
  const warnings: string[] = [];
  const errors: string[] = [];

  if (!process.env.OPENWEATHER_API_KEY) {
    warnings.push('‚ö†Ô∏è  OPENWEATHER_API_KEY ayarlanmamƒ±≈ü. Hava durumu √∂zelliƒüi √ßalƒ±≈ümayacak.');
  }

  if (!process.env.EMAIL_USER) {
    warnings.push('‚ö†Ô∏è  EMAIL_USER ayarlanmamƒ±≈ü. E-posta √∂zellikleri √ßalƒ±≈ümayacak.');
  }

  if (!process.env.EMAIL_PASS) {
    warnings.push('‚ö†Ô∏è  EMAIL_PASS ayarlanmamƒ±≈ü. E-posta √∂zellikleri √ßalƒ±≈ümayacak.');
  }

  if (!process.env.EMAIL_FROM) {
    warnings.push('‚ö†Ô∏è  EMAIL_FROM ayarlanmamƒ±≈ü. E-posta √∂zellikleri √ßalƒ±≈ümayacak.');
  }

  if (warnings.length > 0) {
    console.log('\nüîî Ortam Deƒüi≈ükeni Uyarƒ±larƒ±:');
    warnings.forEach(warning => console.log(warning));
    console.log('üìù .env.example dosyasƒ±nƒ± .env olarak kopyalayƒ±n ve deƒüerlerinizi doldurun.\n');
  }

  if (errors.length > 0) {
    console.error('\n‚ùå Kritik Ortam Deƒüi≈ükeni Hatalarƒ±:');
    errors.forEach(error => console.error(error));
    console.error('Uygulama bu deƒüi≈ükenler olmadan ba≈ülatƒ±lamaz.\n');
    process.exit(1);
  }
}

export function safeGetEnv(key: string, fallback: string = ''): string {
  const value = process.env[key];
  if (!value && !fallback) {
    console.warn(`‚ö†Ô∏è  Ortam deƒüi≈ükeni ${key} ayarlanmamƒ±≈ü ve varsayƒ±lan deƒüeri yok.`);
  }
  return value || fallback;
}


