// CANKIR




import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./depolama";

// Activity logger helper - outputs to stdout for Electron to capture
function logActivity(action: string, description?: string) {
  const timestamp = new Date().toLocaleString('tr-TR', {
    day: '2-digit',
    month: 'long',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
  const message = description 
    ? `[ACTIVITY] ${action} | ${description}`
    : `[ACTIVITY] ${action}`;
  console.log(message);
}

import {
  insertTaskSchema,
  insertMoodSchema,
  insertGoalSchema,
  insertQuestionLogSchema,
  insertExamResultSchema,
  insertFlashcardSchema,
  insertExamSubjectNetSchema,
  insertStudyHoursSchema,
  insertSetupCompletedSchema,
} from "@shared/sema";
import { z } from "zod";
import dotenv from "dotenv";
import nodemailer from "nodemailer";
import fs from "fs";
import path from "path";
dotenv.config();

interface WeatherData {
  main: {
    temp: number;
    temp_max: number;
    temp_min: number;
    humidity: number;
    pressure: number;
    feels_like: number;
  };
  weather: Array<{ id: number; description: string; main: string }>;
  wind: { speed: number; deg: number };
  clouds: { all: number };
  visibility: number;
  sys: {
    sunrise: number;
    sunset: number;
  };
  rain?: { "1h"?: number; "3h"?: number };
  snow?: { "1h"?: number; "3h"?: number };
  cod?: number | string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  // G√∂rev routes
  app.get("/api/tasks", async (req, res) => {
    try {
      const tasks = await storage.getTasks();
      res.json(tasks);
    } catch (error) {
      console.error("Error fetching tasks:", error);
      res.status(500).json({ message: "Failed to fetch tasks" });
    }
  });

  app.post("/api/tasks", async (req, res) => {
    try {
      const validatedData = insertTaskSchema.parse(req.body);
      const task = await storage.createTask(validatedData);
      logActivity('G√∂rev Eklendi', validatedData.title);
      res.status(201).json(task);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res
          .status(400)
          .json({ 
            message: "G√∂rev verisi ge√ßersiz. L√ºtfen t√ºm gerekli alanlarƒ± kontrol edin.", 
            errors: error.errors 
          });
      } else {
        console.error("Error creating task:", error);
        res.status(500).json({ message: "G√∂rev olu≈üturulurken bir hata olu≈ütu. L√ºtfen tekrar deneyin." });
      }
    }
  });

  app.put("/api/tasks/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const validatedData = insertTaskSchema.partial().parse(req.body);
      const task = await storage.updateTask(id, validatedData);

      if (!task) {
        return res.status(404).json({ message: "Task not found" });
      }

      res.json(task);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res
          .status(400)
          .json({ message: "Invalid task data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update task" });
      }
    }
  });

  app.patch("/api/tasks/:id/toggle", async (req, res) => {
    try {
      const { id } = req.params;
      const task = await storage.toggleTaskComplete(id);

      if (!task) {
        return res.status(404).json({ message: "Task not found" });
      }

      logActivity('G√∂rev Durumu Deƒüi≈ütirildi', task.completed ? 'Tamamlandƒ±' : 'Beklemede');
      res.json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to toggle task completion" });
    }
  });

  app.patch("/api/tasks/:id/archive", async (req, res) => {
    try {
      const { id } = req.params;
      const task = await storage.archiveTask(id);

      if (!task) {
        return res.status(404).json({ message: "Task not found" });
      }

      logActivity('G√∂rev Ar≈üivlendi');
      res.json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to archive task" });
    }
  });

  app.patch("/api/tasks/:id/unarchive", async (req, res) => {
    try {
      const { id } = req.params;
      const task = await storage.updateTask(id, { archived: false });

      if (!task) {
        return res.status(404).json({ message: "Task not found" });
      }

      logActivity('G√∂rev Geri Y√ºklendi', 'Ar≈üivden √ßƒ±karƒ±ldƒ±');
      res.json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to unarchive task" });
    }
  });

  app.get("/api/tasks/archived", async (req, res) => {
    try {
      const tasks = await storage.getArchivedTasks();
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch archived tasks" });
    }
  });

  app.get("/api/tasks/by-date-range", async (req, res) => {
    try {
      const { startDate, endDate } = req.query;
      if (!startDate || !endDate) {
        return res.status(400).json({ message: "Start date and end date are required" });
      }
      const tasks = await storage.getTasksByDateRange(startDate as string, endDate as string);
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch tasks by date range" });
    }
  });

  app.delete("/api/tasks/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteTask(id);

      if (!deleted) {
        return res.status(404).json({ message: "G√∂rev bulunamadƒ±. Zaten silinmi≈ü olabilir." });
      }

      logActivity('G√∂rev Silindi');
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting task:", error);
      res.status(500).json({ message: "G√∂rev silinirken bir hata olu≈ütu. L√ºtfen tekrar deneyin." });
    }
  });

  // Ruh hali routes
  app.get("/api/moods", async (req, res) => {
    try {
      const moods = await storage.getMoods();
      res.json(moods);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch moods" });
    }
  });

  app.get("/api/moods/latest", async (req, res) => {
    try {
      const mood = await storage.getLatestMood();
      res.json(mood);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch latest mood" });
    }
  });

  app.post("/api/moods", async (req, res) => {
    try {
      const validatedData = insertMoodSchema.parse(req.body);
      const mood = await storage.createMood(validatedData);
      res.status(201).json(mood);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res
          .status(400)
          .json({ message: "Invalid mood data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create mood" });
      }
    }
  });

  // raporlarƒ±m ve takvim kƒ±smƒ± routes
  app.get("/api/summary/daily", async (req, res) => {
    try {
      const range = parseInt(req.query.range as string) || 30;
      const summary = await storage.getDailySummary(range);
      res.json(summary);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch daily summary" });
    }
  });

  app.get("/api/calendar/:date", async (req, res) => {
    try {
      const { date } = req.params; // YYYY-AA-GG format

      // G√∂revleri getir (ar≈üivlenmi≈ü dahil - takvim i√ßin)
      let activeTasks = [];
      let archivedTasks = [];
      try {
        activeTasks = await storage.getTasksByDate(date) || [];
        archivedTasks = await storage.getArchivedTasks() || [];
      } catch (taskError) {
        console.error("‚ùå Error fetching tasks:", taskError);
        activeTasks = [];
        archivedTasks = [];
      }

      const archivedTasksForDate = archivedTasks.filter((t: any) => {
        if (t.dueDate) {
          const taskDate = t.dueDate.split('T')[0];
          return taskDate === date;
        }
        if (t.createdAt) {
          const createdDate = new Date(t.createdAt).toISOString().split('T')[0];
          return createdDate === date;
        }
        return false;
      });
      const tasksForDate = [...activeTasks, ...archivedTasksForDate];

      // √áalƒ±≈üma saatlerini getir (ar≈üivlenmi≈ü dahil - takvim i√ßin)
      let activeStudyHours = [];
      let archivedStudyHours = [];
      try {
        activeStudyHours = await storage.getStudyHours() || [];
        archivedStudyHours = await storage.getArchivedStudyHours() || [];
      } catch (studyError) {
        console.error("‚ùå Error fetching study hours:", studyError);
        activeStudyHours = [];
        archivedStudyHours = [];
      }

      const allStudyHours = [...activeStudyHours, ...archivedStudyHours];
      const studyHoursForDate = allStudyHours.filter((sh: any) => sh.study_date === date);

      // Soru loglarƒ±nƒ± getir (ar≈üivlenmi≈ü dahil - takvim i√ßin)
      let activeQuestionLogs = [];
      let archivedQuestionLogs = [];
      try {
        activeQuestionLogs = await storage.getQuestionLogs() || [];
        archivedQuestionLogs = await storage.getArchivedQuestionLogs() || [];
      } catch (questionError) {
        console.error("‚ùå Error fetching question logs:", questionError);
        activeQuestionLogs = [];
        archivedQuestionLogs = [];
      }

      const allQuestionLogs = [...activeQuestionLogs, ...archivedQuestionLogs];
      const questionsForDate = allQuestionLogs.filter((q: any) => q.study_date === date);

      // Sƒ±nav sonu√ßlarƒ±nƒ± getir (ar≈üivlenmi≈ü dahil - takvim i√ßin)
      let activeExamResults = [];
      let archivedExamResults = [];
      try {
        activeExamResults = await storage.getExamResults() || [];
        archivedExamResults = await storage.getArchivedExamResults() || [];
      } catch (examError) {
        console.error("‚ùå Error fetching exam results:", examError);
        activeExamResults = [];
        archivedExamResults = [];
      }

      const allExamResults = [...activeExamResults, ...archivedExamResults];
      const examsForDate = allExamResults.filter((e: any) => e.exam_date === date);

      // g√ºnl√ºk kalan g√ºn sayƒ±sƒ± hesaplama - T√ºrkiye saati ile (UTC+3)
      // T√ºrkiye saatine g√∂re bug√ºn√ºn tarihini al
      const now = new Date();
      const formatter = new Intl.DateTimeFormat('en-CA', { 
        timeZone: 'Europe/Istanbul',
        year: 'numeric',
        month: '2-digit', 
        day: '2-digit'
      });
      const istanbulDateStr = formatter.format(now); // YYYY-MM-DD formatƒ±nda
      const [todayYear, todayMonth, todayDay] = istanbulDateStr.split('-').map(Number);
      const today = new Date(todayYear, todayMonth - 1, todayDay);

      // Hedef tarihi parse et
      const [year, month, day] = date.split('-').map(Number);
      const targetDate = new Date(year, month - 1, day);

      const diffTime = targetDate.getTime() - today.getTime();
      // Math.floor kullanarak negatif deƒüerleri doƒüru hesapla (d√ºn = -1, bug√ºn = 0, yarƒ±n = 1)
      const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));

      const response = {
        date,
        dayNumber: targetDate.getDate(),
        daysRemaining: diffDays,
        tasks: tasksForDate,
        tasksCount: tasksForDate.length,
        studyHours: studyHoursForDate,
        questions: questionsForDate,
        exams: examsForDate,
      };

      res.json(response);
    } catch (error) {
      console.error("‚ùå Calendar endpoint error:", error);
      console.error("‚ùå Error details:", {
        name: (error as Error)?.name,
        message: (error as Error)?.message,
        stack: (error as Error)?.stack
      });
      res.status(500).json({ message: "Failed to fetch calendar data", error: (error as Error)?.message });
    }
  });

  // NET HESAPLAMA
  app.post("/api/calculate-ranking", async (req, res) => {
    try {
      const { nets, year } = req.body;

      // nets objesi √∂rneƒüi:
      let tytNets = 0;
      let aytNets = 0;

      // TYT neti hesaplama
      if (nets?.tyt) {
        const tyt = nets.tyt;
        tytNets =
          (parseFloat(tyt.turkce) || 0) +
          (parseFloat(tyt.sosyal) || 0) +
          (parseFloat(tyt.matematik) || 0) +
          (parseFloat(tyt.fen) || 0);
      }

      // AYT neti hesaplama
      if (nets?.ayt) {
        const ayt = nets.ayt;
        aytNets =
          (parseFloat(ayt.matematik) || 0) +
          (parseFloat(ayt.fizik) || 0) +
          (parseFloat(ayt.kimya) || 0) +
          (parseFloat(ayt.biyoloji) || 0);
      }

      // 2023-2025 YKS sƒ±ralama verileri (yakla≈üƒ±k deƒüerler)
      //burasƒ± kullanƒ±lmayacak
      const rankingData: Record<string, any> = {
        "2023": {
          tytWeight: 0.4,
          aytWeight: 0.6,
          rankings: {
            350: 1000,
            320: 5000,
            300: 10000,
            280: 20000,
            260: 35000,
            240: 50000,
            220: 75000,
            200: 100000,
            180: 150000,
            160: 200000,
          },
        },
        "2024": {
          tytWeight: 0.4,
          aytWeight: 0.6,
          rankings: {
            360: 1000,
            330: 5000,
            310: 10000,
            290: 20000,
            270: 35000,
            250: 50000,
            230: 75000,
            210: 100000,
            190: 150000,
            170: 200000,
          },
        },
        "2025": {
          tytWeight: 0.4,
          aytWeight: 0.6,
          rankings: {
            355: 1000,
            325: 5000,
            305: 10000,
            285: 20000,
            265: 35000,
            245: 50000,
            225: 75000,
            205: 100000,
            185: 150000,
            165: 200000,
          },
        },
      };

      const yearData = rankingData[year] || rankingData["2024"];

      // numarasal hatalara kar≈üƒ± kontrol
      if (isNaN(tytNets)) tytNets = 0;
      if (isNaN(aytNets)) aytNets = 0;

      // Net'i puana √ßevirme (yakla≈üƒ±k form√ºl)
      const tytScore = tytNets * 4; // Her doƒüru ~4 puan
      const aytScore = aytNets * 4; // Her doƒüru ~4 puan

      // Aƒüƒ±rlƒ±klƒ± toplam puan
      const totalScore =
        tytScore * yearData.tytWeight + aytScore * yearData.aytWeight;

      // En yakƒ±n sƒ±ralamayƒ± bul
      let estimatedRanking = 500000; // Varsayƒ±lan
      const scores = Object.keys(yearData.rankings)
        .map(Number)
        .sort((a, b) => b - a);

      for (const score of scores) {
        if (totalScore >= score) {
          estimatedRanking = yearData.rankings[score];
          break;
        }
      }

      res.json({
        tytScore: tytScore.toFixed(2),
        aytScore: aytScore.toFixed(2),
        totalScore: totalScore.toFixed(2),
        estimatedRanking,
        year,
        methodology: "2023-2025 YKS verilerine dayalƒ± tahmin",
      });
    } catch (error) {
      console.error("Ranking calculation error:", error);
      res.status(500).json({ message: "Sƒ±ralama hesaplanamadƒ±" });
    }
  });

  // Goal routes
  app.get("/api/goals", async (req, res) => {
    try {
      const goals = await storage.getGoals();
      res.json(goals);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch goals" });
    }
  });

  app.post("/api/goals", async (req, res) => {
    try {
      const validatedData = insertGoalSchema.parse(req.body);
      const goal = await storage.createGoal(validatedData);
      res.status(201).json(goal);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res
          .status(400)
          .json({ message: "Invalid goal data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create goal" });
      }
    }
  });

  app.put("/api/goals/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const validatedData = insertGoalSchema.partial().parse(req.body);
      const goal = await storage.updateGoal(id, validatedData);

      if (!goal) {
        return res.status(404).json({ message: "Goal not found" });
      }

      res.json(goal);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res
          .status(400)
          .json({ message: "Invalid goal data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update goal" });
      }
    }
  });

  app.delete("/api/goals/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteGoal(id);

      if (!deleted) {
        return res.status(404).json({ message: "Goal not found" });
      }

      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete goal" });
    }
  });

  // Sakarya,serdivan i√ßin hava durumu route
  app.get("/api/weather", async (req, res) => {
    try {
      const OPENWEATHER_API_KEY = process.env.OPENWEATHER_API_KEY;

      let currentData: WeatherData;
      let forecastData: any;
      let airQualityData: any;
      let uvData: any;

      if (!OPENWEATHER_API_KEY) {
        // API anahtarƒ± yoksa statik veri kullan
        currentData = {
          main: {
            temp: 18,
            temp_max: 20,
            temp_min: 15,
            humidity: 75,
            pressure: 1013,
            feels_like: 18,
          },
          weather: [{ id: 800, description: "a√ßƒ±k", main: "Clear" }],
          wind: { speed: 2.5, deg: 180 },
          clouds: { all: 20 },
          visibility: 10000,
          sys: {
            sunrise: Math.floor(new Date().setHours(5, 54, 0, 0) / 1000),
            sunset: Math.floor(new Date().setHours(18, 53, 0, 0) / 1000),
          },
        };
        forecastData = { list: [] };
        airQualityData = {
          list: [
            { main: { aqi: 2 }, components: { pm2_5: 15, pm10: 25, o3: 60 } },
          ],
        };
        uvData = { value: 4 };
      } else {
        // Sakarya, Serdivan i√ßin ger√ßek OpenWeather API √ßaƒürƒ±larƒ± (lat: 40.7969, lon: 30.3781)
        const lat = 40.7969;
        const lon = 30.3781;

        try {
          // hava durumu
          const currentResponse = await fetch(
            `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${OPENWEATHER_API_KEY}&units=metric&lang=tr`,
          );
          currentData = await currentResponse.json();

          // API ba≈üarƒ±sƒ±z olursa (ge√ßersiz anahtar vs) statik veri kullan
          if (!currentData || !currentData.main || currentData.cod === 401 || currentData.cod === '401') {
            console.log("Weather API key is invalid, using static data");
            currentData = {
              main: {
                temp: 18,
                temp_max: 20,
                temp_min: 15,
                humidity: 75,
                pressure: 1013,
                feels_like: 18,
              },
              weather: [{ id: 800, description: "a√ßƒ±k", main: "Clear" }],
              wind: { speed: 2.5, deg: 180 },
              clouds: { all: 20 },
              visibility: 10000,
              sys: {
                sunrise: Math.floor(new Date().setHours(5, 54, 0, 0) / 1000),
                sunset: Math.floor(new Date().setHours(18, 53, 0, 0) / 1000),
              },
            };
            forecastData = { list: [] };
            airQualityData = {
              list: [
                { main: { aqi: 2 }, components: { pm2_5: 15, pm10: 25, o3: 60 } },
              ],
            };
            uvData = { value: 4 };
          } else {
            // 5 g√ºnl√ºk tahmin
            const forecastResponse = await fetch(
              `https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&appid=${OPENWEATHER_API_KEY}&units=metric&lang=tr`,
            );
            forecastData = await forecastResponse.json();

            // hava kalitesi
            const airQualityResponse = await fetch(
              `https://api.openweathermap.org/data/2.5/air_pollution?lat=${lat}&lon=${lon}&appid=${OPENWEATHER_API_KEY}`,
            );
            airQualityData = await airQualityResponse.json();

            // uv indeksi
            const uvResponse = await fetch(
              `https://api.openweathermap.org/data/2.5/uvi?lat=${lat}&lon=${lon}&appid=${OPENWEATHER_API_KEY}`,
            );
            uvData = await uvResponse.json();
          }
        } catch (apiError) {
          console.error(
            "OpenWeather API error, falling back to static data:",
            apiError,
          );
          // geriye statik veri d√∂nd√ºr
          currentData = {
            main: {
              temp: 18,
              temp_max: 20,
              temp_min: 15,
              humidity: 75,
              pressure: 1013,
              feels_like: 18,
            },
            weather: [{ id: 800, description: "a√ßƒ±k", main: "Clear" }],
            wind: { speed: 2.5, deg: 180 },
            clouds: { all: 20 },
            visibility: 10000,
            sys: {
              sunrise: Math.floor(new Date().setHours(5, 54, 0, 0) / 1000),
              sunset: Math.floor(new Date().setHours(18, 53, 0, 0) / 1000),
            },
          };
          forecastData = { list: [] };
          airQualityData = {
            list: [
              { main: { aqi: 2 }, components: { pm2_5: 15, pm10: 25, o3: 60 } },
            ],
          };
          uvData = { value: 4 };
        }
      }

      // emoji fonksiyonu
      const getWeatherEmoji = (weatherId: number, isDay: boolean = true) => {
        if (weatherId >= 200 && weatherId < 300) return "‚õàÔ∏è"; // g√∂k g√ºr√ºlt√ºl√º
        if (weatherId >= 300 && weatherId < 400) return "üå¶Ô∏è"; // hafif yaƒümur
        if (weatherId >= 500 && weatherId < 600) return "üåßÔ∏è"; // yaƒümur
        if (weatherId >= 600 && weatherId < 700) return "‚ùÑÔ∏è"; // kar
        if (weatherId >= 700 && weatherId < 800) return "üå´Ô∏è"; // sis
        if (weatherId === 800) return isDay ? "‚òÄÔ∏è" : "üåô"; // a√ßƒ±k
        if (weatherId > 800) return isDay ? "‚õÖ" : "‚òÅÔ∏è"; // bulutlu
        return "üå§Ô∏è";
      };

      // 12 saatlik tahmin i≈üleme
      const hourlyForecast = [];
      const currentHour = new Date().getHours();

      for (let i = 0; i < 12; i++) {
        const hour = (currentHour + i) % 24;
        const isDay = hour >= 6 && hour <= 19;

        // G√ºn boyunca sƒ±caklƒ±k deƒüi≈üimi
        let temp = 18; // Temel sƒ±caklƒ±k
        if (hour >= 6 && hour <= 8)
          temp = 16; // Sabah serin
        else if (hour >= 9 && hour <= 11)
          temp = 19; // Ge√ß sabah sƒ±cak
        else if (hour >= 12 && hour <= 15)
          temp = 21; // √ñƒüle en sƒ±cak
        else if (hour >= 16 && hour <= 18)
          temp = 20; // Ak≈üam serin
        else if (hour >= 19 && hour <= 21)
          temp = 18; // Gece serin
        else temp = 15; // Gece en serin

        // Rastgelelik ekle ama ger√ßek√ßi tut
        temp += Math.floor(Math.random() * 3) - 1; // ¬±1¬∞C

        // Hava durumu ko≈üullarƒ± - √ße≈üitlilik i√ßin karƒ±≈üƒ±m
        let weatherId = 800; // A√ßƒ±k varsayƒ±lan
        let precipitation = 0;

        if (i === 2 || i === 3) {
          weatherId = 801; // Az bulutlu
        } else if (i === 5 || i === 6) {
          weatherId = 802; // Par√ßalƒ± bulutlu
        } else if (i === 8) {
          weatherId = 500; // Hafif yaƒümur
          precipitation = 0.5;
        }

        hourlyForecast.push({
          time: `${hour.toString().padStart(2, "0")}:00`,
          hour: hour,
          temperature: temp,
          emoji: getWeatherEmoji(weatherId, isDay),
          humidity: 75 + Math.floor(Math.random() * 10) - 5, // 70-80% nem
          windSpeed: 8 + Math.floor(Math.random() * 6), // 8-14 km/h r√ºzgar
          windDirection: 180 + Math.floor(Math.random() * 60) - 30, // Deƒüi≈üken r√ºzgar y√∂n√º
          precipitation: precipitation,
          description:
            weatherId === 800
              ? "a√ßƒ±k"
              : weatherId === 801
                ? "az bulutlu"
                : weatherId === 802
                  ? "par√ßalƒ± bulutlu"
                  : "hafif yaƒümur",
        });
      }

      // 7 g√ºnl√ºk tahmin i≈üleme
      const dailyForecast: any[] = [];
      const today = new Date();

      // √ñzel g√ºnler i√ßin tahmin verileri
      const customForecast = [
        // Bug√ºn - mevcut hava durumunu kullan
        {
          date: today.toISOString().split("T")[0],
          dayName: today.toLocaleDateString("tr-TR", { weekday: "short" }),
          temperature: {
            max: Math.round(
              currentData.main.temp_max || currentData.main.temp + 3,
            ),
            min: Math.round(
              currentData.main.temp_min || currentData.main.temp - 3,
            ),
          },
          description: currentData.weather[0].description,
          emoji: getWeatherEmoji(currentData.weather[0].id),
          humidity: currentData.main.humidity,
          windSpeed: Math.round(currentData.wind.speed * 3.6),
        },
      ];

      // 6 g√ºnl√ºk √∂zel tahmin verisi
      for (let i = 1; i <= 6; i++) {
        const forecastDate = new Date(today);
        forecastDate.setDate(today.getDate() + i);
        const dayName = forecastDate.toLocaleDateString("tr-TR", {
          weekday: "short",
        });

        let weatherData;
        switch (dayName.toLowerCase()) {
          case "√ßar": // carsamba
            weatherData = {
              temperature: { max: 18, min: 12 },
              description: "sis",
              emoji: "üå´Ô∏è",
              humidity: 85,
              windSpeed: 8,
            };
            break;
          case "per": // per≈üembe
            weatherData = {
              temperature: { max: 19, min: 13 },
              description: "g√∂kg√ºr√ºlt√ºl√º saƒüanak",
              emoji: "‚õàÔ∏è",
              humidity: 80,
              windSpeed: 15,
            };
            break;
          case "cum": // cuma
            weatherData = {
              temperature: { max: 19, min: 13 },
              description: "g√∂kg√ºr√ºlt√ºl√º saƒüanak",
              emoji: "‚õàÔ∏è",
              humidity: 78,
              windSpeed: 12,
            };
            break;
          case "cmt": // cumartesi
            weatherData = {
              temperature: { max: 18, min: 12 },
              description: "yaƒümurlu",
              emoji: "üåßÔ∏è",
              humidity: 88,
              windSpeed: 10,
            };
            break;
          case "paz": // pazar
            weatherData = {
              temperature: { max: 19, min: 13 },
              description: "g√∂kg√ºr√ºlt√ºl√º saƒüanak",
              emoji: "‚õàÔ∏è",
              humidity: 82,
              windSpeed: 14,
            };
            break;
          default:
            // diƒüer g√ºnler i√ßin genel tahmin
            weatherData = {
              temperature: { max: 20, min: 14 },
              description: "par√ßalƒ± bulutlu",
              emoji: "‚õÖ",
              humidity: 65,
              windSpeed: 8,
            };
        }

        customForecast.push({
          date: forecastDate.toISOString().split("T")[0],
          dayName: dayName,
          ...weatherData,
        });
      }

      // custom forecast'u dailyForecast'a ekle
      dailyForecast.push(...customForecast);

      // hava durumu detaylarƒ±
      const now = new Date();
      const sunrise = new Date(currentData.sys.sunrise * 1000);
      const sunset = new Date(currentData.sys.sunset * 1000);
      const isDay = now > sunrise && now < sunset;

      // UV indeksi hesaplama (ger√ßek UV API'si ba≈üarƒ±sƒ±z olursa yedek)
      const getUVIndex = () => {
        if (uvData && uvData.value !== undefined) {
          const uvValue = Math.round(uvData.value);
          let level, description;

          if (uvValue <= 2) {
            level = "D√º≈ü√ºk";
            description = "G√ºvenli seviyede, koruma gereksiz";
          } else if (uvValue <= 5) {
            level = "Orta";
            description = "Orta seviye risk, g√ºne≈ü kremi √∂nerilir";
          } else if (uvValue <= 7) {
            level = "Y√ºksek";
            description = "Koruyucu √∂nlemler gerekli";
          } else if (uvValue <= 10) {
            level = "√áok Y√ºksek";
            description = "G√º√ßl√º koruma ≈üart, g√∂lgeyi tercih edin";
          } else {
            level = "A≈üƒ±rƒ±";
            description = "Dƒ±≈üarƒ± √ßƒ±kmaktan ka√ßƒ±nƒ±n";
          }

          return { value: uvValue, level, description };
        }

        // uv API yoksa basit hesaplama
        if (!isDay)
          return {
            value: 0,
            level: "D√º≈ü√ºk",
            description: "Gece boyunca UV endeksi d√º≈ü√ºk",
          };
        const hour = now.getHours();
        if (hour < 8 || hour > 18)
          return { value: 1, level: "D√º≈ü√ºk", description: "G√ºvenli seviyede" };
        if (hour >= 10 && hour <= 16) {
          const baseUV =
            currentData.clouds.all < 30
              ? 8
              : currentData.clouds.all < 70
                ? 5
                : 3;
          return baseUV > 7
            ? {
                value: baseUV,
                level: "Y√ºksek",
                description: "Koruyucu √∂nlemler gerekli",
              }
            : { value: baseUV, level: "Orta", description: "Orta seviye risk" };
        }
        return { value: 3, level: "Orta", description: "Orta seviye risk" };
      };

      // hava kalitesi hesaplama
      const airQuality = airQualityData
        ? {
            aqi: airQualityData.list[0].main.aqi,
            level:
              ["ƒ∞yi", "Orta", "Hassas", "Saƒülƒ±ksƒ±z", "√áok Saƒülƒ±ksƒ±z"][
                airQualityData.list[0].main.aqi - 1
              ] || "Bilinmiyor",
            description:
