        // Arka planda ge√ßen s√ºreyi hesapla
        const startTime = parseInt(savedStartTime);
        const elapsed = Date.now() - startTime;
        const remainingTime = Math.max(0, state.time - elapsed);
        return {
          ...state,
          time: remainingTime
        };
      }
      return state;
    }

    return {
      time: 25 * 60 * 1000,
      isRunning: false,
      isBreak: false,
      currentCycle: 1,
      mode: 'work' as 'work' | 'break' | 'longBreak'
