              const topicGroups = allCompletedRaw.reduce((acc: any, item: any) => {
                // Key'den subject ve topic bilgisini √ßƒ±kar (format: "subject-topic")
                let subject = item.subject;
                let topic = item.topic;

                // Eƒüer subject veya topic yoksa, key'den parse et
