                    subject = subject || keyParts[0] || capitalizeSubjectName(keyParts[0]) || 'Genel';
                    topic = topic || keyParts.slice(1).join('-') || normalizeTopic(keyParts.slice(1).join('-')) || 'Konu Belirtilmemi≈ü';
