```typescript
// server/field-mapping.ts

// 1. Mapping table
export const FIELD_MAP_EN_TO_TR: Record<string, string> = {
  exam_name: "sinavAdi",
  exam_date: "sinavTarihi",
  tyt_net: "tytNet",
  // ... tek yerden y√∂netim
